mapper.select:
select * from l_loan
where 1 = 1

    and productName = #{productName}


    and amountMin >= #{amountMin}


    and #{amountMax} >= amountMax


    and TO_DAYS(createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(createTime)


    and status = #{status}



        and tag like #{tag,jdbcType=INTEGER}


order by createTime desc
limit #{num},#{pageSize}

select count(0)
where 1 = 1

    and productName = #{productName}


    and amountMin >= #{amountMin}


    and #{amountMax} >= amountMax


    and TO_DAYS(createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(createTime)


    and status = #{status}



        and tag like #{tag,jdbcType=INTEGER}

select * from c_card
where 1 = 1

    and bankId = #{bankId}


    and cardName = #{cardName}


    and level = #{level}


    and moneyType = #{moneyType}


    and cardOrganization = #{cardOrganization}


    and annualFeeType = #{annualFeeType}


    and privilege = #{privilege}


    and cardCoverType = #{cardCoverType}


    and status = #{status}


    and TO_DAYS(createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(createTime)

order by creatTime desc
limit #{num},#{pageSize}

select count(0) from c_card
where 1 = 1

    and bankId = #{bankId}


    and cardName = #{cardName}


    and level = #{level}


    and moneyType = #{moneyType}


    and cardOrganization = #{cardOrganization}


    and annualFeeType = #{annualFeeType}


    and privilege = #{privilege}


    and cardCoverType = #{cardCoverType}


    and status = #{status}


    and TO_DAYS(createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(createTime)
