mapper.select:
select count(0)
from u_user
where userPhone = #{userPhone}

mapper.update:
update u_user_code
set code = #{code},createTime = now()
where userPhone = #{userPhone}

update u_user
set passWord = #{passWord}
where userPhone = #{userPhone}

mapper.select:
select id,userUrl,status
from u_user
where userPhone = #{userPhone} and passWord = #{passWord}

mapper.update:
